const ItemCount = () => {

    let [suma, setSuma] = useState(1);
    let [resta, setResta] = useState(1)



    const sumar = () => {
            setSuma(suma + 1)
    }

    const restar = () =>{
        setResta( suma - 1)
    }

  return (

    <div className="container my-5" >
        <h2>Counter</h2>
        <hr />
        <p onClick={sumar} >{suma}</p>
        <button className="btn btn-primary" onClick={sumar} >sumar</button>
        <hr />
        <button className="btn btn-primary" onClick={restar} >sumar</button>
        <p onClick={restar} >{resta}</p>
    </div>
  )
}
export default ItemCount
