import { useState, useEffect } from "react"


const ItemCount = ({init, stock, onAdd}) => {

  const [contador, setContador] = useState(init)
  const [confirmado, setConfirmado] = useState(false)


  useEffect(()=>{
    console.log('Efecto infinito')
  },[confirmado])

  const sumar = () => {
    setContador(contador+1)
  }

  const restar = () => {
    setContador(contador-1)
  }

  const confirmar = () => {
    onAdd(contador)
    setConfirmado(true)
  }

  if(!confirmado){
  return (
    <div>

        <p>Unidades a comprar: {contador}</p>
        <button onClick={sumar} className="material-icons" >Add</button>
        <button onClick={confirmar} >Confirmar</button>
        <button onClick={restar} className="material-icons">Rest</button>

    </div>

  )
}
else{
  return (
    <div>

        <p>Unidades a comprar: {contador}</p>
        <button onClick={sumar} className="material-icons" >Add</button>
        <button onClick={confirmar} >Confirmar</button>
        <button onClick={restar} className="material-icons">Rest</button>
        <p className="modal" > Se confirmaron {contador} Unidades </p>

    </div>

  )
}
}
export default ItemCount
